mapper.select:
SELECT s.sbb_ID , temperature_A temperatureA,temperature_B temperatureB,temperature_C temperatureC,temperature_N temperatureN, current_A currentA, current_B currentB, current_C currentC, voltage_A voltageA, voltage_B voltageB, voltage_C voltageC, power_factor powerFactor, active_power activePower, electrical_degree electricalDegree, leakage_electricity leakageElectricity, sbb_name sbbName, create_date createDate FROM tb_real_data s LEFT JOIN tb_device t ON t.ID=s.sbb_ID WHERE 1=1
AND s.sbb_ID= #{sbbId}
order by create_date desc

SELECT TOP 20 s.sbb_ID , temperature_A temperatureA,temperature_B temperatureB,temperature_C temperatureC,temperature_N temperatureN, current_A currentA, current_B currentB, current_C currentC, voltage_A voltageA, voltage_B voltageB, voltage_C voltageC, electrical_degree electricalDegree, leakage_electricity leakageElectricity, power_factor powerFactor, active_power activePower, sbb_name sbbName, create_date createDate FROM tb_real_data s LEFT JOIN tb_device t ON t.ID=s.sbb_ID WHERE 1=1
AND s.sbb_ID= #{sbbId}
AND create_date >= #{dateFrom}
AND create_date <= #{dateTo}
order by create_date desc

SELECT TOP 1 s.sbb_ID , temperature_A temperatureA,temperature_B temperatureB,temperature_C temperatureC,temperature_N temperatureN, current_A currentA, current_B currentB, current_C currentC, voltage_A voltageA, voltage_B voltageB, voltage_C voltageC, power_factor powerFactor, active_power activePower, electrical_degree electricalDegree, leakage_electricity leakageElectricity, sbb_name sbbName, create_date createDate FROM tb_real_data s LEFT JOIN tb_device t ON t.ID=s.sbb_ID WHERE 1=1
AND s.sbb_ID= #{sbbId}
order by create_date desc
